import { useState } from 'react';
import words from "./words.json"
import Modal from 'react-modal';
import './App.css';

const WORD_SIZE = 5;
let currentIndex = 0;
const randomIndex = Math.floor(Math.random() * words.length);
let wordToGuess = words[randomIndex].toUpperCase();
console.log(wordToGuess)
function App() {
  const [guesses, setGuesses] = useState(Array(6).fill(null));
  const [currentGuess, setCurrentGuess] = useState('')
  const [isGuessed, setIsGuessed] = useState(false)
  const [isGameOver, setIsGameOver] = useState(false)

  const resetGame = () => {
    currentIndex = 0;
    setIsGameOver(false);
    setIsGuessed(false);
    setGuesses(Array(6).fill(null));
    wordToGuess = words[randomIndex].toUpperCase();
  };

  return (
    <div className="App">
      <h1 className='title'>Wordle</h1>
      {
        guesses.map(guess => {
          return <Line guess={guess ?? ''}/>
        })
      }
      <form onSubmit={(e) => {
        e.preventDefault();
        if (currentGuess.length === 5) {
          let newGuesses = [...guesses];
          newGuesses[currentIndex] = currentGuess.toUpperCase();
          setCurrentGuess('');
          setGuesses(newGuesses)
          currentIndex++;
        }
      }}>

        <label for="input">Guess:</label>
        <input 
          className='input'
          type='text'value={currentGuess} 
          onChange={(e) => {
            const guess = e.target.value;
            if (guess.length < 6 && guess.match(/^[a-zA-Z]*$/))
              setCurrentGuess(e.target.value.toUpperCase());
          }}
          onKeyDown={(e) => {
            if (e.key === 'Enter') {
              if (currentGuess.length === 5) {
                console.log(currentGuess, '-+-+-', wordToGuess)
                if (currentGuess === wordToGuess) {
                  setIsGameOver(true);
                  setIsGuessed(true);
                }
                else if (currentIndex === 5)
                  setIsGameOver(true);
                let newGuesses = [...guesses];
                newGuesses[currentIndex] = currentGuess.toUpperCase();
                setCurrentGuess('');
                setGuesses(newGuesses)
                currentIndex++;
              }
            }
          }} 
        />
        <button type="submit">Submit</button>
      </form>
      <Modal
        isOpen={isGameOver}
        onRequestClose={resetGame}
        contentLabel="Game Over Modal"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
          },
        }}
      >
        <h2>{isGuessed ? 'Congratulations!' : 'Game Over'}</h2>
        <p>{isGuessed ? 'You guessed the word!' : `Better luck next time! The word was "${wordToGuess}".`}</p>
        <button onClick={resetGame}>Play Again</button>
      </Modal>
      <section className="how-to-play">
    <h2>How to Play</h2>
    <p>Guess the Wordle in 6 tries</p>
    <ol>
      <li>Enter a valid  in the input field.</li>
      <li>Click the "Submit" button or press Enter to submit your guess.</li>
      <li>Your guess will be checked and feedback will be provided:</li>
      <ul>
        <li className="feedback"><strong>Green:</strong> The letter is in the correct position.</li>
        <li className="feedback"><strong>Yellow:</strong> The letter is in the word but in the wrong position.</li>
        <li className="feedback"><strong>Gray:</strong> The letter is not in the word at all.</li>
      </ul>
      <li>Use the feedback to adjust your next guess.</li>
      <li>Try to guess the correct word within the allowed number of attempts.</li>
    </ol>
  </section>
    </div>
  );
}

function Line({ guess }) {
  let char = ''
  let boxes = []
  let className = 'tile';
  for (let i = 0; i < WORD_SIZE; i++) {
    console.log(guess, '---', wordToGuess)
    if (guess && wordToGuess[i] === guess[i])
      className += ' correct';
    else if (guess && wordToGuess.includes(guess[i]))
      className += ' close';
    else if (guess)
      className += ' incorrect';
    char = <div className={className}>{guess[i]}</div>;
    className = 'tile';
    boxes.push(char);
  }

  return <div className='line'>{boxes}</div>
}

export default App;
