// src/utils/wordUtils.js
export const checkGuess = (guess, targetWord) => {
    let feedback = Array(guess.length).fill(''); // Initialize feedback array

    // First pass: Check for correct letters in correct positions
    for (let i = 0; i < guess.length; i++) {
      if (guess[i] === targetWord[i]) {
        feedback[i] = 'correct-position';
      }
    }

    // Second pass: Check for correct letters in wrong positions
    for (let i = 0; i < guess.length; i++) {
      if (feedback[i] === '') {
        if (targetWord.includes(guess[i])) {
          feedback[i] = 'wrong-position';
        } else {
          feedback[i] = 'incorrect-position';
        }
      }
    }

    return { correct: guess === targetWord, feedback };
  };
