// src/App.js
useEffect(() => {
  const savedGame = JSON.parse(localStorage.getItem('wordleGame'));
  if (savedGame) {
    setTargetWord(savedGame.targetWord);
    setGuesses(savedGame.guesses);
    setAttemptsLeft(savedGame.attemptsLeft);
    setGameStatus(savedGame.gameStatus);
  } else {
    setTargetWord(generateRandomWord());
  }
}, []);

useEffect(() => {
  localStorage.setItem('wordleGame', JSON.stringify({
    targetWord,
    guesses,
    attemptsLeft,
    gameStatus
  }));
}, [targetWord, guesses, attemptsLeft, gameStatus]);
