import './App.css'
import { useEffect, useState } from 'react'

const API_URL = '/words.json';
const WORD_LENGHT = 5;

function App() {
  const [solution, setSolution] = useState('hello');
  const [guesses, setGuesses] = useState(Array(6).fill(null));
  const [currentGuess, setcurrentGuess] = useState('');
  const [isGameOver, setIsGameOver] = useState(false);

  useEffect(() => {
    const handleType = (event) => {

      if (isGameOver) {
        return;
      }

      if (event.key === 'Enter') {
        if (currentGuess.length !== 5) {
          return ;
        }
        const newGuesses = [...guesses];
        newGuesses[guesses.findIndex(val => val == null)] = currentGuess;
        setGuesses(newGuesses);
        setcurrentGuess('');

        const isCorrect = solution === currentGuess
        // console.log('solution: ', solution);
        // console.log('isCorrect: ', isCorrect);
        if (isCorrect) {
          // console.log('correct+++++');
          setIsGameOver(true);
          // setSolution('')
        }
      }

      if (event.key === 'Backspace') {
        setcurrentGuess(currentGuess.slice(0, -1));
        return ;
      }

      if (currentGuess.length >= 5) {
        return;
      }

      const isLetter = event.key.match(/^[a-z]{1}$/) != null;
      if (isLetter) {
        setcurrentGuess(currentGuess + event.key);
      }

    };

    window.addEventListener('keydown', handleType);

    return () => window.addEventListener('keydown', handleType);
  }, [currentGuess, isGameOver, solution, guesses]);

  useEffect(() => {
    const fetchWords = async () => {
      const response = await fetch(API_URL);
      if (!response.ok) {
        throw new Error('response was not ok');
      }
      const words = await response.json();
      // console.log('word: ', words[1]);
      const randomword = words[Math.floor(Math.random() * words.length)];
      setSolution(randomword.toLowerCase());
      // console.log(words);
    };

    fetchWords();
  }, []);

  return (
    <body>
      <h1>{solution}</h1>
    <div className="board">
      {
        guesses.map((guess, i) => {
          const iscurrentGess = i === guesses.findIndex(val => val == null);
          return (
            <Line 
              guess={iscurrentGess ? currentGuess : guess ?? ''} 
              isFinal={!iscurrentGess && guess != null}
              solution={solution}
            />
          );
        })}
    </div>

    </body>
  );
}

export default App;


function Line({guess, isFinal, solution}) {
  const tiles = [];

  for (let i = 0; i < WORD_LENGHT; i++) {
    const char = guess[i];
    let className = 'tile';

    if (isFinal) {
      console.log(solution);
      if (char === solution[i]) {
        className += ' correct';
      } else if (solution.includes(char)) {
        className += ' close';
      } else {
        className += ' incorrect';
      }
    }

    tiles.push(
      <div key={i} className={className}>
        {char}
      </div>)
  }
  return <div className='line'>{tiles}</div>
}
