import './App.css'
import { useEffect, useState } from 'react'

const API_URL = '/words.json';
const WORD_LENGHT = 5;

function App() {
  const [solution, setSolution] = useState('');
  const [arr, setArr] = useState([]);
  const [guesses, setGuesses] = useState(Array(6).fill(null));
  const [currentGuess, setcurrentGuess] = useState('');
  const [isGameOver, setIsGameOver] = useState(false);
  useEffect(() => {
    const handleType = (event) => {

      if (isGameOver) {
        return;
      }

      if (event.key === 'Enter') {
        if (currentGuess.length !== 5) {
          return ;
        }
        const isCorrect = solution === currentGuess;
        if (isCorrect) {
          setIsGameOver(true);
        }
      }

      if (event.key === 'Backspace') {
        setcurrentGuess(currentGuess.slice(0, -1));
        return ;
      }

      if (currentGuess.length >= 5) {
        return;
      }

      setcurrentGuess(currentGuess + event.key);
    };

    window.addEventListener('keydown', handleType);

    return () => window.addEventListener('keydown', handleType);
  }, [currentGuess]);

  useEffect(() => {
    const fetchWords = async () => {
      const response = await fetch(API_URL);
      if (!response.ok) {

        throw new Error('response was not ok');
      }
      const words = await response.text();
      // console.log('word: ', words[0]);
      const randomword = words[Math.floor(Math.random() % words.length)];
      setSolution(randomword);
      // console.log(words);
    };

    fetchWords();
  }, []);

  return (
    <div className="board">
    <h1> {solution} </h1>
      {
        guesses.map((guess, i) => {
          const iscurrentGess = i === guesses.findIndex(val => val == null);
          return (
            <Line guess={iscurrentGess ? currentGuess : guess ?? ''} />
          );
        })}
        {currentGuess}
    </div>
  );
}

export default App;


function Line({guess}) {
  const tiles = [];

  for (let i = 0; i < WORD_LENGHT; i++) {
    const char = guess[i];
    tiles.push(<div key={i} className='tile'>{char}</div>)
  }
  return <div className='line'>{tiles}</div>
}
