import { useState } from 'react';
import words from "./words.json"
import './App.css';

const WORD_SIZE = 5;
let currentIndex = 0;
const randomIndex = Math.floor(Math.random() * words.length);
const wordToGuess = words[randomIndex];
console.log(wordToGuess)
function App() {
  const [guesses, setGuesses] = useState(Array(6).fill(null));
  const [currentGuess, setCurrentGuess] = useState('')

  return (
    <div className="App">
      <h1 className='title'>Wordle</h1>
      {
        guesses.map(guess => {
          return <Line guess={guess ?? ''}/>
        })
      }
      <input 
        className='input'
        type='text'value={currentGuess} 
        onChange={(e) => {
          setCurrentGuess(e.target.value);
        }}
        onKeyDown={(e) => {
          if (e.key === 'Enter') {
            if (currentGuess.length === 5 && currentGuess.match(/^[a-zA-Z]{5}$/)) {
              let newGuesses = [...guesses];
              newGuesses[currentIndex] = currentGuess;
              setCurrentGuess('');
              setGuesses(newGuesses)
              currentIndex++;
            }
          }
        }}></input>
    </div>
  );
}

function Line({ guess }) {
  let char = ''
  let boxes = []
  let className = 'incorrect';
  for (let i = 0; i < WORD_SIZE; i++) {
    console.log()
    if (wordToGuess[i] === guess[i])
      className = 'correct';
    else if (wordToGuess.includes(guess[i]))
      className = 'close';
    char = <div className={className}>{guess[i]}</div>;
    boxes.push(char);
  }

  return <div className='line'>{boxes}</div>
}

export default App;
