import { useState } from 'react';
import './App.css';

const WORD_SIZE = 5;
const guess = "";
const [guesses, setGuesses] = useState(Array(6).fill(null))

function App() {
  return (
    <div className="App">
      {
        guesses.map(guess => {
          return <Line guess={guess ?? ''}/>
        })
      }
    </div>
  );
}

function Line({ guess }) {
  let char = ''
  let boxes = []
  for (let i = 0; i < WORD_SIZE; i++) {
    char = <div>guess[i]</div>;
    boxes.push(char);
  }

  return <div>boxes</div>
}

export default App;
