import React from 'react';

const GameStatus = ({ status, word, attemptsLeft }) => {
  return (
    <div>
      {status === 'won' && <p>Congratulations! You've won!</p>}
      {status === 'lost' && <p>Game over! The word was {word}.</p>}
      {status === 'playing' && <p>Attempts left: {attemptsLeft}</p>}
    </div>
  );
};

export default GameStatus;
