import React, { useState } from 'react';
import Modal from 'react-modal';
import 'HowToPlay.css'

// Setting the app element for accessibility
Modal.setAppElement('#root');

const HowToPlay = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);

  return (
    <div>
      <button onClick={openModal} style={styles.button}>How to Play</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="How to Play"
        style={customStyles}
      >
        <h2>How to Play</h2>
        <p>Guess the Wordle in 6 tries</p>
        <ol>
        <li>Enter a valid 5-letter word in the input field.</li>
        <li>Click the "Submit" button or press Enter to submit your guess.</li>
        <li>The color of the tiles will change to show how close your guess was to the word.</li>
        <ul>
            <li className="feedback"><strong>Green:</strong> The letter is in the correct position.</li>
            <li className="feedback"><strong>Yellow:</strong> The letter is in the word but in the wrong position.</li>
            <li className="feedback"><strong>Gray:</strong> The letter is not in the word at all.</li>
        </ul>
        </ol>
        <button onClick={closeModal} style={styles.closeButton}>Close</button>
      </Modal>
    </div>
  );
};

export default HowToPlay;
