import React, { useState, useEffect } from 'react';
import GuessInput from './components/GuessInput';
import GuessList from './components/GuessList';
import GameStatus from './components/GameStatus';
import Header from './components/Header';
import { generateRandomWord, checkGuess } from './utils/wordUtils';
import './App.css'

const MAX_ATTEMPTS = 6;

function App() {
  const [targetWord, setTargetWord] = useState('');
  const [guesses, setGuesses] = useState([]);
  const [attemptsLeft, setAttemptsLeft] = useState(MAX_ATTEMPTS);
  const [gameStatus, setGameStatus] = useState('playing'); // playing, won, lost

  useEffect(() => {
    setTargetWord(generateRandomWord());
  }, []);

  const handleGuess = (word) => {
    if (gameStatus !== 'playing') return;

    const result = checkGuess(word, targetWord);
    setGuesses([...guesses, { word, result }]);
    setAttemptsLeft(attemptsLeft - 1);

    if (result.correct) {
      setGameStatus('won');
    } else if (attemptsLeft - 1 === 0) {
      setGameStatus('lost');
    }
  };

  return (
    <div className="App">
      <Header />
      <GuessInput onGuess={handleGuess} disabled={gameStatus !== 'playing'} />
      <GuessList guesses={guesses} />
      <GameStatus status={gameStatus} word={targetWord} attemptsLeft={attemptsLeft} />
    </div>
  );
}

export default App;
